import sys


def split_data(input_file, validation_file, classification_file):
    with open(input_file, "r") as file:
        lines = file.readlines()

    with open(validation_file, "w") as validation_file, \
            open(classification_file, "w") as classification_file:
        for line in lines:
            line = line.strip()  # Remove any leading/trailing whitespace or newline characters
            validation_file.write(line[:-1] + "\n")
            classification_file.write(line[-1] + "\n")


